import React, { useEffect, useState } from "react";
import "./App.css"; // Importing the CSS file for styling

function App() {
  const [posts, setPosts] = useState([]);
  const [username, setUsername] = useState("");
  const [content, setContent] = useState("");

  // Fetch posts from the backend API
  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/posts/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setPosts(data);
      })
      .catch((error) => {
        console.error("There was a problem with the fetch operation:", error);
      });
  }, []);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    const newPost = { username, content };

    fetch("http://127.0.0.1:8000/api/posts/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPost),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to post new blog");
        }
        return response.json();
      })
      .then((data) => {
        // Add the new post to the top of the posts list
        setPosts([data, ...posts]);
        setUsername(""); // Clear the input fields
        setContent("");
      })
      .catch((error) => {
        console.error("Error while posting new blog:", error);
      });
  };

  return (
    <div className="app-container">
      <h1 className="header">Blog Posts</h1>

      {/* Form to add a new post */}
      <form className="post-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="label">Username:</label>
          <input
            className="input"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label className="label">Content:</label>
          <textarea
            className="textarea"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit" className="submit-button">
          Post
        </button>
      </form>

      {/* List of posts */}
      <div className="posts-container">
        {posts.map((post, index) => (
          <div className="post" key={index}>
            <div className="post-header">
              <strong>{post.username}</strong>
              <small>{new Date(post.created_at).toLocaleString()}</small>
            </div>
            <p className="post-content">{post.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
