import React, { useEffect, useState } from "react";
import "./App.css"; // Import the CSS file

function App() {
  const [posts, setPosts] = useState([]);
  const [username, setUsername] = useState("");
  const [content, setContent] = useState("");

  // Fetch posts from the backend API
  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/posts/") // Replace with your backend URL
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setPosts(data);
      })
      .catch((error) => {
        console.error("There was a problem with the fetch operation:", error);
      });
  }, []);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    const newPost = { username, content };

    fetch("http://127.0.0.1:8000/api/posts/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPost),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to post new blog");
        }
        return response.json();
      })
      .then((data) => {
        setPosts([data, ...posts]);
        setUsername("");
        setContent("");
      })
      .catch((error) => {
        console.error("Error while posting new blog:", error);
      });
  };

  return (
    <div className="app-container">
      <h1 className="header">Modern Blog</h1>

      {/* Form to add a new post */}
      <form className="form-container" onSubmit={handleSubmit}>
        <input
          type="text"
          className="input-field"
          placeholder="Your Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <textarea
          className="textarea-field"
          placeholder="Write your blog post here..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        ></textarea>
        <button className="submit-button" type="submit">
          Post
        </button>
      </form>

      {/* List of posts */}
      <div className="posts-container">
        {posts.map((post, index) => (
          <div key={index} className="post-card">
            <h3>{post.username}</h3>
            <p>{post.content}</p>
            <small>{new Date(post.created_at).toLocaleString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
